import 'package:flutter/material.dart';

class Assignment3 extends StatefulWidget {
  const Assignment3({super.key});
  @override
  State createState() => _Assignment3State();
}

class _Assignment3State extends State {
  int? _selectedIndex = 0;
  final List<String> imageList = [
    "data:image/jpeg;base64,",
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT2jOmdq_-dbxaXa3oPJV8vZjVKsBLwG131zA&usqp=CAU&reload=on",
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTrHmh9WoqPc3t2zQubuOMR_Ng2lv308pctPw&usqp=CAU"
  ];
  void _showNextImage() {
    setState(() {
      if (_selectedIndex! < 2) {
        _selectedIndex = _selectedIndex! + 1;
      } else {
        _selectedIndex = 0;
      }
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text("Assignment 1"),
      ),
      body: Center(
        child: Column(mainAxisAlignment: MainAxisAlignment.center, children: [
          Image.network(
            imageList[_selectedIndex!],
            width: 300,
            height: 300,
          ),
          SizedBox(
            height: 20,
          ),
          ElevatedButton(onPressed: _showNextImage, child: Text("next")),
          SizedBox(
            height: 20,
          ),
        ]),
      ),
    );
  }
}